import { useEffect, useState } from "react";
import User from '../../../../api/user';
import { useNavigate } from 'react-router-dom';
import UserDisplay from './userDisplay';

const UsersList = () => {

    const [users, setUsers] = useState([])

    const userApi = User();
    const navigate = useNavigate();
    
    useEffect(() => {

        const ConfirmUserRole = async () => {
            const token = localStorage.getItem('jwtToken');
            try {
                const user = await userApi.GetUsername(token);

                if (user.data.user.role != 'ADMIN') {
                    throw Error;
                }

                //get the users
                const users = await userApi.GetUserData(token);
                setUsers(users.data);

            } catch (error) {
                //token expired or invalid navigate to Login
                localStorage.removeItem('jwtToken');
                navigate('/');
            }
        }
        ConfirmUserRole();
        
    }, []);


    return (
        <div className="UserList-Container">
            {users.map((user, index) => (
                <div key={index} className="UserDisplay">
                <UserDisplay
                    email={user.email}
                    password={user.password}
                    deviceId={user.deviceId}
                    enirisEmail={"thisworks@gmail.com"}
                />
                </div>
            ))}
        </div>
    )

}

export default UsersList;
