import { useEffect, useState } from 'react';
import './login.css'
import {Link, useNavigate} from "react-router-dom";
import User from '../../api/user';
import SlantedImage from '../../Images/Schermafbeelding 2023-08-16 164524slanted.png'
import Logo from '../../Images/firn-embleem-brown.png';

const Login = () => {

    const navigate = useNavigate();
    const userApi = User();
    const [errorMessages, setErrorMessages] = useState({})
    const [Succesfull, setSuccesfull] = useState(false);
    const [Failed, setFailed] = useState(false);

    const renderErrorMessage = (name) => name === errorMessages.name && (<div className='error'>{errorMessages.message}</div>);

    const KeepLoggedIn = () => {
        const token = localStorage.getItem('jwtToken');
        if (token) {
            console.log('JWT token found:', token);
            // You can use the token for further processing
            //login using token
        } else {
            console.log('No JWT token found in localStorage');
        }
    }

    useEffect(() => {
        KeepLoggedIn();
    })

    const handleSubmit = async (event) => {
        event.preventDefault();
        //check if user exisits

        setFailed(false)
        setSuccesfull(false)

        var { email, pass, remember } = document.forms[0];

        const response = await userApi.login(email.value, pass.value, remember.value);
        console.log(remember.value);
        if (response.status == 204) {
            setFailed(true);
            
        } else {
            setSuccesfull(true);
            localStorage.setItem('jwtToken', response.data);
            navigate('/HomePage');
        }

    };

    return (
        
        <div className="container">
            <div className="image-container">
                <img className='LoginImage' src={SlantedImage} alt="Desktop Website" /> 
                {/* <div class="image-container-chat">
                    <img class="image image-front" src={SlantedImage} alt="Image 1"/>
                    <img class="image image-back" src={SlantedImage} alt="Image 2"/>
                </div> */}
            </div>
            <div className="login-container">
                <img width={150} src={Logo} alt="" />
                <h1 className='Login-Header'>Login</h1>
                <form className='LoginForm' onSubmit={handleSubmit}>
                    <div className="input-group">
                        <label className='input-Label'>Email </label>
                        <input className='input-Field' type="text" name="email" required />
                        {renderErrorMessage("email")}
                    </div>
                    <div className="input-group">
                        <label className='input-Label'>Password </label>
                        <input className='input-Field' type="password" name="pass" required />
                        {renderErrorMessage("pass")}
                    </div>
                    <div>
                        <input type="checkbox" name='remember'/>
                        <label className='input-Checkbox-Label'>Remember me</label>
                    </div>
                    <button className='Login-form-button' type="submit">Login</button>
                    {Succesfull ? <div className='LoginSuccesfull'>Loggin Succesfull!</div> : <></>}
                    {Failed ? <div className='LoginFailed'>Loggin Failed!</div> : <></>}
                </form>
                
            </div>
        </div>
        
    );

}

export default Login;
